const http = require("https");

const options = {
  "method": "GET",
  "hostname": "api.rootly.com",
  "port": null,
  "path": "/v1/alerts/%7Balert_id%7D/events?include=SOME_STRING_VALUE&page%5Bnumber%5D=SOME_INTEGER_VALUE&page%5Bsize%5D=SOME_INTEGER_VALUE&filter%5Bkind%5D=SOME_STRING_VALUE&filter%5Baction%5D=SOME_STRING_VALUE",
  "headers": {
    "Authorization": "Bearer REPLACE_BEARER_TOKEN"
  }
};

const req = http.request(options, function (res) {
  const chunks = [];

  res.on("data", function (chunk) {
    chunks.push(chunk);
  });

  res.on("end", function () {
    const body = Buffer.concat(chunks);
    console.log(body.toString());
  });
});

req.end();

